
var mongo = require('mongo');
var db = mongo.db;
var products = mongo.products;
var user = mongo.user;


function get_retailer_info(name,callback){
var promise = user.find({business_name:name});
promise.on('complete',function(err,docs){
callback(docs);
});
}

module.exports = {


get_product_retailers : function(name,callback){
var promise = products.find({name:name});
var retailer = '';
promise.on('complete',function(err,docs){
if(docs.length > 0){
retailer = docs[0]['retailers'];
}
get_retailer_info(retailer,function(retailer_docs){
callback(docs,retailer_docs);
});
});
},




}
